import axios from "axios";
import { Cart } from "../model/Cart";
import { Order } from "../model/order";
let localHost: string = 'http://localhost:3000'
class OrderController {
    checkout(ItemOrder: Order): Promise<Order> {
        return axios.post(`${localHost}/order/checkout`, { ItemOrder })
    }
    getListOrder() {
        return axios.get(`${localHost}/order/checkout/list`).then(res => {
            return res.data
        })
    }
    async cart(id: string, quantity: number, price: number) {
        return axios.post(`${localHost}/order/${id}`, { quantity, price })
    }
    async listCart() {
        return axios.get(`${localHost}/listCart`).then(res => { return res.data })
    }
}
export const orderController = new OrderController();
