import React from 'react'
import { formatMoney } from '../../../components/helper'
import { Cart } from '../../../model/Cart'
import { OrderWithDetail } from '../../../model/order'
import './ListProductOrder.css'
export default function ListProductOrder(props : props) {
    return (

            <div className="containerITem">
               <img src={props.orderWithDetail.orderProducts[0].product?.image} alt="" />
                <h3 className="nameItemOrder">{props.orderWithDetail.orderProducts[0].product?.image}</h3>
                <p className="quantity"> {props.orderWithDetail.orderProducts[0].quantity}</p>
                <p className="priceItemOrder">{formatMoney(props.orderWithDetail.orderProducts[0].quantity)} vnd</p>
                <p className='totalMoney'>{formatMoney(props.orderWithDetail.orderProducts[0].quantity *props.orderWithDetail.orderProducts[0].quantity)} vnd </p>
            </div>

    )
}
interface props {
    orderWithDetail : OrderWithDetail
}
