import React, { useState } from 'react'
import { Product, listProducts } from '../../../model/Product'
import Model from '../Modal/Model'
import ProductItem from '../product/ProductItem'
import './Reset.css'
import { v4 as uuidv4 } from 'uuid';
import Filter from '../../../components/filter/Filter'

export default function TableProduct() {

    const [listProduct, setListProduct] = useState<Product[]>(listProducts)
    const [edit, setEdit] = useState<Product>({ id: '', name: '', image: '', price: 1 })
    const [filter, setFilter] = useState<Product>({ id: '', name: '', image: '', price: 1 })
    const onDelete = (id: string): void => {
        let tam = (listProduct.filter(item => item.id != id))
        setListProduct(tam)
        localStorage.setItem('product', JSON.stringify(tam))
    }
    const setDataEdit = (product: Product) => {

        setEdit({ ...product })
    }
    const setDataFilter = (product: Product) => {

        setFilter({ ...product })
    }
    const onData = (product: Product) => {
        if (product.id != '') {
            listProduct.forEach((item, index) => {
                if (item.id === edit.id) {
                    listProduct[index] = { ...product }
                }
            })
            localStorage.setItem('product', JSON.stringify(listProduct))
        }
        else {
            product = { ...product, id: uuidv4() }
            let tam = listProduct.slice();
            tam.push(product)
            localStorage.setItem('product', JSON.stringify(tam))

            setListProduct(tam)
        }
        setEdit({ id: '', name: '', image: '', price: 1 })
    }
    const onFilter = (name: string) => {
        let tam = (listProduct.filter(item => item.name === name))

        setListProduct(tam)
    }

    return (
        <div>

            <Model key={uuidv4()} onAdd={onData} dataEdit={edit} />
            <Filter data={filter} onFilter={onFilter} />
            <div className="sticky-table">

                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>Image</th>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Xóa</th>
                            <th>Sửa</th>

                        </tr>
                    </thead>
                    <tbody>
                        {listProduct.map((item, index) => <ProductItem setDataEdit={setDataEdit} handleDelete={onDelete} product={item} key={index} />)}
                    </tbody>
                </table>
            </div>

        </div>
    )
}
