import axios from "axios";
let localHost: string = 'http://localhost:3000'
const accessToken = localStorage.getItem('jwt')
export const authAxios = axios.create({
  baseURL: localHost,
  timeout: 10000,
  headers:{
      authorization : `${accessToken}`,
      'Content-Type': 'application/json'
  }
})
