import axios from "axios";
let localHost: string = 'http://localhost:3000'
const accessToken = localStorage.getItem('jwt') || ""
 const authAxios = axios.create({
  baseURL: localHost,
  timeout: 10000,
})
authAxios.defaults.headers.common['authorization'] = accessToken;
export{authAxios}
