import axios from "axios";
let localHost: string = 'http://localhost:3000'
 const authAxios = axios.create({
  baseURL: localHost,
  timeout: 10000,
})
authAxios.defaults.headers.common['authorization'] = localStorage.getItem('jwt') || "";
export{authAxios}
