import React, { useState } from 'react'
import './Admin.css'
import FromProduct from '../../components/fromProduct/FromProduct'
import { Product, listProducts } from '../../model/Product'
import ProductItem from '../../components/productItem/ProductItem'
import { v4 as uuidv4 } from 'uuid';
export default function Admin() {
    let listProductLocal = []
    let localTam = localStorage.getItem('product')
    if (localTam !== null) {
        listProductLocal = JSON.parse(localTam)
    }
    const [listProduct, setListProduct] = useState<Product[]>(listProductLocal)
    const [edit, setEdit] = useState<Product>({ id: '', name: '', image: '', price: 1 })

    const onDelete = (id: string): void => {
        let tam = (listProduct.filter(item => item.id != id))
        setListProduct(tam)
        localStorage.setItem('product', JSON.stringify(tam))
    }
    const setDataEdit = (product: Product) => {
        console.log(edit.id);
        setEdit({ ...product })
    }
    const onData = (product: Product) => {
        if (product.id != '') {
            listProduct.forEach((item ,index)=> {
                if (item.id === edit.id) {
                   listProduct[index]={...product}
                }
            })
            localStorage.setItem('product', JSON.stringify(listProduct))
        }
        else {
            product = { ...product, id: uuidv4() }
            let tam = listProduct.slice();
            tam.push(product)
            localStorage.setItem('product', JSON.stringify(tam))
            setListProduct(tam)
        }
       // setEdit({ id: '', name: '', image: '', price: 1 })
    }
    return (

        <div>
            <div className="khoiPhai">

                <section id="dssp">
                    {
                        listProduct.map((item, index) => <ProductItem key={index} handleDelete={() => onDelete(item.id)} setDataEdit={setDataEdit} product={item} />)
                    }

                </section>
            </div>
            <FromProduct key={uuidv4()} onAdd={onData} dataEdit={edit} />
        </div>
    )
}
