import React, { useState } from 'react'
import './Admin.css'
import FromProduct from '../../components/fromProduct/FromProduct'
import { Product, listProducts} from '../../model/Product'
import ProductItem from '../../components/productItem/ProductItem'
import { v4 as uuidv4 } from 'uuid';
export default function Admin() {
    const [listProduct,setListProduct]=useState<Product[]>(listProducts)
    const [edit,setEdit] =useState<Product>({id: '',name :'',image:'',price: 1})

    const onDelete = (id: string): void => {
        setListProduct(listProduct.filter(item => item.id != id))
    }
    const setDataEdit =(product: Product)=>{
        setEdit({...product})       
    }
    const onData =(product : Product)=>{
        let tam = listProduct.slice();
        product.id =uuidv4();
        tam.push(product)
        console.log(product);   

        setListProduct(tam)
    }
    return (

        <div>
            <div className="khoiPhai">

                <section id="dssp">
                    {
                        listProduct.map((item,index) => <ProductItem key={index} handleDelete={()=>onDelete(item.id)} setDataEdit={setDataEdit}  product={item} />)
                    }

                </section>
            </div>
            <FromProduct key={uuidv4()} onAdd={onData} dataEdit={edit}/>
        </div>
    )
}
