import React, { useState } from 'react'
import { Product } from '../../../model/Product'
import './Model.css'
export default function Model(props : ChildComponentProps) {
    const [newProduct,setNewProduct] = useState<Product>(props.dataEdit)
    const [checkImg,setCheckImage] = useState<boolean>(false)
    const [checkName,setCheckName] = useState<boolean>(false)
    const [checkPrice,setCheckPrice] = useState<boolean>(false)
    const img = checkImg == false ? 'Vui long nhap anh' : ''
    const name = checkImg == false ? 'Vui long nhap anh' : ''
    const gia = checkImg == false ? 'Vui long nhap anh' : ''
    const onBlur = (node : string) =>{
        if(node === 'img'){
            newProduct.image.length < 5 ? setCheckImage(false) : setCheckImage(true)
        }else if(node === 'name'){
            newProduct.name.length < 5 || newProduct.name.length > 20 ? setCheckName(false) : setCheckName(true)
        }
    }

    return (
        <div>
            <div className="wrapper">
                <a href="#demo-modal ">Thêm sản phẩm</a>
        </div>
            <div id="demo-modal" className="modal">
                <div className="modal__content">
                    <h1>Modal thêm sản phẩm </h1>
                    <label htmlFor="">Hình ảnh</label>
                    <input type="text"name='hinhanh' onBlur={()=>onBlur('img')} onChange={e=>{setNewProduct({...newProduct,image:e.target.value})}} value={newProduct.image}/>
                   <small></small>
                    <label htmlFor="">Tên sản phẩm</label>
                    <input type="text" name='ten' onBlur={()=>onBlur('name')} onChange={e=>{setNewProduct({...newProduct,name:e.target.value})}} value={newProduct.name}/>
                    <label htmlFor="">Giá</label>
                    <input type="text" name='gia' onBlur={()=>onBlur('price')} onChange={e=>{setNewProduct({...newProduct,price:Number(e.target.value)})}}value={newProduct.price}/>
                    <div className="modal__footer">
                    <button className='btnAddd' onClick={()=>props.onAdd(newProduct)}>Thêm sản phẩm</button>
                    </div>
                    <a href="#" className="modal__close close">×</a>
                </div>
            </div>
        </div>

    )
}
interface ChildComponentProps{
    onAdd : (product : Product) =>void,
    dataEdit : Product
}
